mapper.select:
SELECT 

FROM t_db_platform a

WHERE a.id = #{id}

SELECT 

FROM t_db_platform a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_db_platform a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_db_platform SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM t_db_platform a


	a.del_flag = #{DEL_FLAG_NORMAL}



