mapper.select:
SELECT 

FROM t_user a

WHERE a.id = #{id}

SELECT 

FROM t_user a


	a.del_flag = #{DEL_FLAG_NORMAL}

		and a.t_loginname=#{tLoginname}


		and a.t_name=#{tName}


		and a.t_phone=#{tPhone}


		and a.t_userid=#{tUserid}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

